import { gql } from '@apollo/client';

export const PRODUCT_FRAGMENT = gql`
  fragment ProductFields on Product {
    id
    name
    description
  }
`;

export const GET_PRODUCT = gql`
  query {
    products {
      totalItems
      items {
        ...ProductFields
        featuredAsset {
          source
          width
          height
        }
        variants {
          price
          productId
        }
      }
    }
  }
  ${PRODUCT_FRAGMENT}
`;

export const ADD_ITEM_TO_ORDER = gql`
  mutation AddItemToOrder($productVariantId: ID!, $quantity: Int!):ProductsResponse {
    addItemToOrder(productId: $productId, quantity: $quantity) {
      id
      name
      quantity
      # Include any other fields you may need from the order item
    }
  }
`;
