import { gql } from '@apollo/client';

export const GET_PRODUCT = gql`
  query {
    products {
      totalItems
      items {
        id
        name
        description
        featuredAsset {
          source
          width
          height
        }
        variants {
          price
          productId
        }
      }
    }
  }
`;
