import { useQuery } from '@apollo/client';
import { GET_PRODUCT } from '../graphql/productQuery';
import {
  Grid,
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
} from '@mui/material';

export function ProductList() {
  const { data, loading, error } = useQuery(GET_PRODUCT);
  console.log(data, 'data');
  console.log(error, 'error');
  console.log(loading, 'loading');

  //  const onlyThenProducts = data.products.items.slice(44);
  //  console.log(onlyThenProducts, 'onlyThenProducts');
  return (
    <Grid container spacing={4} sx={{ padding: 4 }}>
      {products.map((product) => (
        <Grid item key={product.id} xs={12} sm={6} md={4}>
          <Card>
            <CardMedia
              component="img"
              height="140"
              image={product.featuredAsset.source}
              alt={product.name}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {product.name}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {product.description}
              </Typography>
              <Typography variant="h6" color="primary">
                ${product.variants[0].price / 100}{' '}
                {/* Assuming price is in cents */}
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small">View More</Button>
              <Button size="small">Add to Cart</Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}
