import { useMutation, useQuery } from '@apollo/client';
import { ADD_ITEM_TO_ORDER, GET_PRODUCTS } from '../graphql/productQuery';
import {
  Grid,
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
  CircularProgress,
  Alert,
} from '@mui/material';
import { ProductsResponse } from '../interfaces/products';
import { useOrder } from '../store/orderProvider';

export function ProductList() {
  const { addToOrder } = useOrder();
  const { data, loading, error } = useQuery<ProductsResponse>(GET_PRODUCTS, {
    variables: { options: { take: 20 } },
  });
  const [
    addItemToOrder,
    { data: mutationData, loading: MutationLoading, error: mutationError },
  ] = useMutation(ADD_ITEM_TO_ORDER, {
    refetchQueries: [
      {
        query: GET_PRODUCTS,
      },
    ],
    onCompleted: (data) => {
      addToOrder(data?.addItemToOrder);
    },
  });

  if (loading) {
    return (
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        style={{ height: '100vh' }}
      >
        <CircularProgress />
      </Grid>
    );
  }

  if (error) {
    return (
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        style={{ height: '100vh' }}
      >
        <Alert severity="error">
          An error occurred while fetching products: {error.message}
        </Alert>
      </Grid>
    );
  }

  return (
    <Grid container spacing={4} sx={{ padding: 4 }}>
      {data?.products?.items.map((product) => (
        <Grid item key={product.id} xs={12} sm={6} md={4}>
          <Card>
            <CardMedia
              component="img"
              height="140"
              image={product.featuredAsset.source}
              alt={product.name}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {product.name}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {product.description}
              </Typography>
              {product.variants.map((variant, index: number) => (
                <Typography key={index} variant="h6" color="primary">
                  ${variant.price / 100} {/* Assuming price is in cents */}
                </Typography>
              ))}
            </CardContent>
            <CardActions>
              <Button
                onClick={() =>
                  addItemToOrder({
                    variables: { productVariantId: product.id, quantity: 1 },
                  })
                }
                size="small"
              >
                Buy
              </Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}
