import { gql } from '@apollo/client';

export const PRODUCT_FRAGMENT = gql`
  fragment ProductFields on Product {
    id
    name
    description
  }
`;

export const GET_PRODUCT = gql`
  query {
    products {
      totalItems
      items {
        ...ProductFields
        featuredAsset {
          source
          width
          height
        }
        variants {
          price
          productId
        }
      }
    }
  }
  ${PRODUCT_FRAGMENT}
`;
