import {
  Grid,
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
  CircularProgress,
  Alert,
  Snackbar,
} from '@mui/material';

import useProductList from '../hooks/useProductList';

export function ProductList() {
  const {
    productsList,
    loading,
    error,
    snackbarOpen,
    handleBuyClick,
    handleSnackbarClose,
    MutationLoading,
  } = useProductList();

  if (loading) {
    return (
      <Grid
        data-testid="loading-spinner"
        container
        justifyContent="center"
        alignItems="center"
        style={{ height: '100vh' }}
      >
        <CircularProgress />
      </Grid>
    );
  }

  if (error) {
    return (
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        style={{ height: '100vh' }}
      >
        <Alert severity="error">
          An error occurred while fetching products: {error.message}
        </Alert>
      </Grid>
    );
  }

  return (
    <Grid container spacing={4} sx={{ padding: 4 }}>
      {productsList?.map((product) => (
        <Grid
          item
          key={product.id}
          xs={12}
          sm={6}
          md={4}
          sx={{
            '&:hover': {
              boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.2)',
            },
          }}
        >
          <Card>
            <CardMedia
              component="img"
              height="140"
              image={product.featuredAsset.source}
              alt={product.name}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {product.name}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {product.description}
              </Typography>
              {product.variants.map((variant, index: number) => (
                <Typography key={index} variant="h6" color="primary">
                  ${variant.price / 100} {/* Assuming price is in cents */}
                </Typography>
              ))}
            </CardContent>
            <CardActions>
              <Button
                variant="contained"
                data-testid="buy-button-1"
                onClick={() => handleBuyClick(product.id)}
                size="small"
                disabled={MutationLoading}
              >
                Buy
              </Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={handleSnackbarClose}
        message={'An error occurred while adding the item to the order'}
      />
    </Grid>
  );
}
