import { useMutation, useQuery } from '@apollo/client';
import { GET_PRODUCT } from '../graphql/productQuery';
import {
  Grid,
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
  CircularProgress,
  Alert,
} from '@mui/material';
import { ProductsResponse } from '../interfaces/products';

export function ProductList() {
  const { data, loading, error } = useQuery<ProductsResponse>(GET_PRODUCT);
  const {
    data: mutationData,
    loading: MutationLoading,
    error: mutationError,
  } = useMutation<unknown>(GET_PRODUCT);

  console.log(mutationData, 'mutationData');
  console.log(MutationLoading, 'MutationLoading');
  console.log(mutationError, 'mutationError');

  if (loading) {
    return (
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        style={{ height: '100vh' }}
      >
        <CircularProgress />
      </Grid>
    );
  }

  if (error) {
    return (
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        style={{ height: '100vh' }}
      >
        <Alert severity="error">
          An error occurred while fetching products: {error.message}
        </Alert>
      </Grid>
    );
  }

  return (
    <Grid container spacing={4} sx={{ padding: 4 }}>
      {data?.products?.items.map((product) => (
        <Grid item key={product.id} xs={12} sm={6} md={4}>
          <Card>
            <CardMedia
              component="img"
              height="140"
              image={product.featuredAsset.source}
              alt={product.name}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div">
                {product.name}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {product.description}
              </Typography>
              {product.variants.map((variant, index: number) => (
                <Typography key={index} variant="h6" color="primary">
                  ${variant.price / 100} {/* Assuming price is in cents */}
                </Typography>
              ))}
            </CardContent>
            <CardActions>
              <Button size="small">Buy</Button>
            </CardActions>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}
